using FASTX


sequence = ""

function catalyze(sequence, regex_pattern)
    return [x for x in split(sequence, regex_pattern)]
end

EcoRI_pattern = r"GAATTC"

open(FASTA.Reader, "GCA_940337035.1_PGI_AGRIOTES_LIN_V1_genomic.fna",) do reader
    for record in reader
        ## Do something
        # like showing the identifiers
        x = catalyze(FASTX.sequence(record), EcoRI_pattern)
        print(x)
    end
end
